package class_19_12_25;

public class CompoundOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		
		System.out.println(a+=10);
		System.out.println(a-=10);
		System.out.println(a*=10);
		System.out.println(a/=10);
		System.out.println(a%=10);

	}

}
